Lifebook_Ingest_Overview.dashboard_body:
{
  "widgets": [
    {
      "type": "alarm",
      "x": 0,
      "y": 0,
      "width": 24,
      "height": 4,
      "properties": {
        "title": "Ingest - INCIDENT (composite)",
        "alarms": [
          "arn:aws:cloudwatch:us-east-1:354630286254:alarm:lifebook-ingest-incident"
        ]
      }
    },
    {
      "type": "alarm",
      "x": 0,
      "y": 4,
      "width": 24,
      "height": 4,
      "properties": {
        "title": "Ingest - component alarms",
        "alarms": [
          "arn:aws:cloudwatch:us-east-1:354630286254:alarm:lifebook-ingest-quiet-15m",
          "arn:aws:cloudwatch:us-east-1:354630286254:alarm:lifebook-ingest-errors-5m",
          "arn:aws:cloudwatch:us-east-1:354630286254:alarm:lifebook-ingest-throttles-5m"
        ]
      }
    },
    {
      "type": "metric",
      "x": 0,
      "y": 8,
      "width": 24,
      "height": 8,
      "properties": {
        "view": "timeSeries",
        "stacked": false,
        "region": "us-east-1",
        "title": "lifebook-ingest - Invocations / Errors / Throttles (Sum, 60s)",
        "metrics": [
          [
            "AWS/Lambda",
            "Invocations",
            "FunctionName",
            "lifebook-ingest",
            {
              "stat": "Sum",
              "period": 60
            }
          ],
          [
            "AWS/Lambda",
            "Errors",
            "FunctionName",
            "lifebook-ingest",
            {
              "stat": "Sum",
              "period": 60
            }
          ],
          [
            "AWS/Lambda",
            "Throttles",
            "FunctionName",
            "lifebook-ingest",
            {
              "stat": "Sum",
              "period": 60
            }
          ]
        ]
      }
    },
    {
      "type": "log",
      "x": 0,
      "y": 16,
      "width": 24,
      "height": 7,
      "properties": {
        "region": "us-east-1",
        "title": "lifebook-ingest - recent errors (Logs Insights)",
        "query": "SOURCE '/aws/lambda/lifebook-ingest' | filter @message like /(?i)(error|exception|fail)/ | fields @timestamp, @message, @logStream | sort @timestamp desc | limit 50"
      }
    },
    {
      "type": "metric",
      "x": 0,
      "y": 23,
      "width": 12,
      "height": 6,
      "properties": {
        "stat": "Average",
        "period": 60,
        "setPeriodToTimeRange": false,
        "sparkline": false,
        "region": "us-east-1",
        "stacked": false,
        "view": "timeSeries",
        "legend": {
          "position": "bottom"
        },
        "metrics": [
          "AWS/SQS",
          "ApproximateNumberOfMessagesVisible",
          "QueueName",
          "lifebook-ingest-dlq"
        ],
        "title": "Ingest DLQ — Visible (1m)"
      }
    },
    {
      "type": "metric",
      "x": 0,
      "y": 29,
      "width": 12,
      "height": 6,
      "properties": {
        "stat": "Maximum",
        "period": 60,
        "setPeriodToTimeRange": false,
        "sparkline": false,
        "region": "us-east-1",
        "stacked": false,
        "view": "timeSeries",
        "legend": {
          "position": "bottom"
        },
        "metrics": [
          "AWS/SQS",
          "ApproximateAgeOfOldestMessage",
          "QueueName",
          "lifebook-ingest-dlq"
        ],
        "title": "Ingest DLQ — OldestAge (1m)"
      }
    }
  ]
}
